function hodorize(e){if(e.nodeType===Node.TEXT_NODE){var t=e.textContent;var n=t.replace(/\b(\w|-|\u00d6|\00f6){3,}\b/ig,function(){var e=Math.floor(Math.random()*words.length);return words[e]});e.textContent=n;return}else if(e.nodeType===Node.ELEMENT_NODE){for(var r=0;r<e.childNodes.length;++r){hodorize(e.childNodes[r])}return}else{return}}var words=["hodoor","hodor","hö-dor","HODOÖOR","hodo-hodo"];hodorize(document.body);